<script setup>
const open = ref(false);
import { gsap, CSSPlugin } from 'gsap/all'
gsap.registerPlugin(CSSPlugin);

const $header = ref(null);
const route = useRoute();

function headerShow() {
  return gsap.to($header.value, { y: 0, duration: 0.5 });
}

function headerHide() {
  return gsap.to($header.value, { y: -$header.value.clientHeight, duration: 0.5 });
}
watch(() => route.path, async () => {
  await headerHide();
  open.value = false;
  await headerShow();
});
</script>
  
